import React, { useState, useEffect, useRef } from 'react';
import './AccountSwitcher.css';

const AccountSwitcher = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [accounts, setAccounts] = useState([]);
  const [currentAccount, setCurrentAccount] = useState(null);
  const dropdownRef = useRef(null);

  // Load saved accounts on component mount
  useEffect(() => {
    const loadAccounts = () => {
      const savedAccounts = JSON.parse(localStorage.getItem('savedAccounts')) || [];
      setAccounts(savedAccounts);

      // Check if current user is logged in
      const token = localStorage.getItem('authToken');
      const email = localStorage.getItem('userEmail');
      const username = localStorage.getItem('username');
      const userId = localStorage.getItem('userId');
      const userRole = localStorage.getItem('userRole');

      if (token && email) {
        setCurrentAccount({
          email,
          username: username || email.split('@')[0],
          userId,
          role: userRole || 'user'
        });
      } else {
        setCurrentAccount(null);
      }
    };

    loadAccounts();

    // Add event listener to check for login/logout events
    window.addEventListener('storage', loadAccounts);
    return () => window.removeEventListener('storage', loadAccounts);
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Save current account
  const saveCurrentAccount = () => {
    if (!currentAccount) return;

    // Check if account already exists
    const existingIndex = accounts.findIndex(acc => acc.email === currentAccount.email);
    let updatedAccounts = [...accounts];

    if (existingIndex >= 0) {
      // Update existing account
      updatedAccounts[existingIndex] = currentAccount;
    } else {
      // Add new account
      updatedAccounts.push(currentAccount);
    }

    localStorage.setItem('savedAccounts', JSON.stringify(updatedAccounts));
    setAccounts(updatedAccounts);
  };

  // Remove account
  const removeAccount = (index) => {
    const updatedAccounts = accounts.filter((_, i) => i !== index);
    localStorage.setItem('savedAccounts', JSON.stringify(updatedAccounts));
    setAccounts(updatedAccounts);
  };

  // Switch to another account
  const switchToAccount = (account) => {
    // Save current account first if logged in
    if (currentAccount) {
      saveCurrentAccount();
    }

    // Log out current user
    localStorage.removeItem('authToken');
    localStorage.removeItem('userEmail');
    localStorage.removeItem('userId');
    localStorage.removeItem('userRole');
    localStorage.removeItem('username');
    localStorage.removeItem('isAuthenticated');

    // Save the account data to sessionStorage for login page to use
    sessionStorage.setItem('switchToAccount', JSON.stringify(account));

    // Redirect to login page
    window.location.href = '/login';

    setIsOpen(false);
  };

  // Get initials for avatar
  const getInitials = (name) => {
    return name ? name.charAt(0).toUpperCase() : '?';
  };

  // Get background color based on user role
  const getAvatarColor = (role) => {
    switch (role) {
      case 'admin':
        return '#f85444';
      default:
        return '#ff9a3c';
    }
  };

  return (
    <div className="account-switcher-container" ref={dropdownRef}>
      <button 
        className="account-switcher-button"
        onClick={() => setIsOpen(!isOpen)}
        title="Account Switcher"
      >
        {currentAccount ? (
          <div 
            className="avatar"
            style={{ backgroundColor: getAvatarColor(currentAccount.role) }}
          >
            {getInitials(currentAccount.username)}
          </div>
        ) : (
          <div className="avatar default-avatar">?</div>
        )}
      </button>

      {isOpen && (
        <div className="account-dropdown">
          <div className="dropdown-header">
            <h3>Accounts</h3>
          </div>

          {currentAccount && (
            <div className="current-account">
              <div 
                className="avatar"
                style={{ backgroundColor: getAvatarColor(currentAccount.role) }}
              >
                {getInitials(currentAccount.username)}
              </div>
              <div className="account-info">
                <div className="account-name">{currentAccount.username}</div>
                <div className="account-email">{currentAccount.email}</div>
              </div>
              <button 
                className="save-account-btn"
                onClick={saveCurrentAccount}
                title="Save this account"
              >
                +
              </button>
            </div>
          )}

          {accounts.length > 0 && (
            <div className="saved-accounts">
              <div className="section-title">Saved Accounts</div>
              <ul className="accounts-list">
                {accounts.map((account, index) => (
                  <li key={index} className="account-item">
                    <div 
                      className="avatar"
                      style={{ backgroundColor: getAvatarColor(account.role) }}
                    >
                      {getInitials(account.username)}
                    </div>
                    <div 
                      className="account-info"
                      onClick={() => switchToAccount(account)}
                    >
                      <div className="account-name">{account.username}</div>
                      <div className="account-email">{account.email}</div>
                    </div>
                    <button 
                      className="remove-account-btn"
                      onClick={() => removeAccount(index)}
                      title="Remove account"
                    >
                      Ã—
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}

          <div className="dropdown-footer">
            <button 
              className="close-btn"
              onClick={() => setIsOpen(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountSwitcher;
